metrics.mql:
TIME-RANGE {duration:120s, etime:$etime}

CATEGORY telegraf_apachepulsar_broker
TAGLOAD {backward:true}

SELECT [ 'pcode', 'time', 'oid', 'pulsar_storage_size', 'topic' ]
FILTER { key: "pulsar_storage_size", exist: true }
FILTER { key: "topic", exist: true }
FIRST-ONLY {key:[pcode, topic]}
CREATE { key: _id_, expr: "topic" }
GROUP { timeunit:120s, pk:_id_ }

UPDATE { key: "pulsar_storage_size", value: sum }

ORDER {key:[pulsar_storage_size, topic], sort: [desc, asc] }
DELETE [pcode, oid, time]
FIRST-ONLY {key: _id_ }
LIMIT 10

