INJECT timepast
HEADER { "95thPercentile$":"#" }
OIDSET { oid:$oid, okind:$okind, onode:$onode }
CATEGORY {"telegraf_kafka_server":6h, "telegraf_kafka_server{m5}":3d, "telegraf_kafka_server{h1}":unlimit } 
TAGLOAD
INJECT default
UPDATE { key: ['95thPercentile'], value: $TIME_MERGE_PLACE }
SELECT [ 'pcode', 'time', 'oid', '95thPercentile', 'pname', 'type', 'name' ]
FILTER { key: "95thPercentile", exist: true }
FILTER { key: "pname", exist: true }
FILTER { key: "type", value: "ZooKeeperClientMetrics"}
FILTER { key: "name", value: "ZooKeeperRequestLatencyMs"}

CREATE { key:_id_, expr: "pcode+'_'+pname" }
CREATE { key:_name_, expr: "pname" }
GROUP { timeunit: 10s, merge: ['95thPercentile'], pk: _id_, last: _name_ }
UPDATE { key: ['95thPercentile'], value: $OBJECT_MERGE_PLACE }

