metrics.mql:
INJECT timepast
HEADER { "usage_average$":"#" }
OIDSET { oid:$oid, okind:$okind, onode:$onode }
CATEGORY {"telegraf_vsphere_host_cpu":6h, "telegraf_vsphere_host_cpu{m5}":3d, "telegraf_vsphere_host_cpu{h1}":unlimit } 
TAGLOAD
INJECT default
UPDATE { key: ['usage_average'], value: $TIME_MERGE_PLACE }
SELECT [ 'pcode', 'time', 'oid', 'usage_average', 'esxhostname', 'cpu' ]
FILTER { key: "usage_average", exist: true }
FILTER { key: "esxhostname", exist: true }
FILTER { key: "cpu", exist: true }
FILTER { key: "cpu", value: "instance-total" }

CREATE { key:_id_, expr: "pcode+'_'+esxhostname+'_'+cpu" }
CREATE { key:_name_, expr: "esxhostname+' / '+cpu" }
GROUP { timeunit: 5s, merge: ['usage_average'], pk: _id_, last: _name_ }
UPDATE { key: ['usage_average'], value: $OBJECT_MERGE_PLACE }

