INJECT default

UPDATE { key: ['rwtotal','read_average','write_average'], value: $TIME_MERGE_PLACE }
SELECT [ 'pcode', 'time', 'oid', 'read_average', 'write_average', 'dcname', 'dsname' ]
FILTER { key: "read_average", exist: true }
FILTER { key: "write_average", exist: true }
FILTER { key: "dcname", exist: true }
FILTER { key: "dsname", exist: true }

CREATE { key:_id_, expr: "pcode+'_'+dcname+'_'+dsname" }
CREATE { key:_name_, expr: "dcname+' / '+dsname" }
CREATE { key:RWTotal, expr: "read_average + write_average"}

GROUP { timeunit: 5s, merge: ['read_average','write_average','RWTotal'], pk: _id_, last: _name_ }
UPDATE { key: ['RWTotal','_rwtotal_','read_average','write_average'], value: $OBJECT_MERGE_PLACE }
