INJECT timepast
HEADER { "usage_average$":"#" }
OIDSET { oid:$oid, okind:$okind, onode:$onode }
CATEGORY {"telegraf_vsphere_vm_cpu":6h, "telegraf_vsphere_vm_cpu{m5}":3d, "telegraf_vsphere_vm_cpu{h1}":unlimit } 
TAGLOAD
INJECT default
UPDATE { key: ['usage_average'], value: $TIME_MERGE_PLACE }
SELECT [ 'pcode', 'time', 'oid', 'usage_average', 'clustername' ]
FILTER { key: "usage_average", exist: true }

CREATE { key:_id_, expr: "pcode+'/'+clustername" }
CREATE { key:_name_, expr: "clustername" }
GROUP { timeunit: 5s, merge: ['usage_average'], pk: _id_, last: _name_ }
UPDATE { key: ['usage_average'], value: $OBJECT_MERGE_PLACE }

