INJECT timepast
HEADER { "uptime_latest$":"#" }
TIME-RANGE {recent:120s}
CATEGORY "telegraf_vsphere_vm_sys"
TAGLOAD {backward:true}

SELECT [ 'pcode', 'time', 'oid', 'uptime_latest', 'vmname', 'clustername' ]
FILTER { key: "uptime_latest", exist: true }
FILTER { key: "vmname", exist: true }
FIRST-ONLY {key:[vmname, clustername]}
CREATE { key:_id_, expr: "pcode+'_'+vmname+'_'+clustername" }
CREATE { key:_name_, expr: "vmname+' / '+clustername" }
ORDER {key:uptime_latest, sort: desc}

